Analytics.Script:
CREATE TEMPORARY TABLE AndroidDataData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_ANDROID_DATA_STATS", incrementalParams "ORG_WSO2_IOT_ANDROID_DATA_STATS, DAY");
CREATE TEMPORARY TABLE Android_Data_Stat_Per_Day USING CarbonAnalytics OPTIONS (tableName "Android_Data_Stat_Per_Day", schema "owner STRING, deviceId STRING, type STRING, total LONG -i, year INT -i, month INT -i, day INT -i, timestamp STRING", primaryKeys "year, month, day, deviceId, owner, type", mergeSchema "false");
INSERT INTO TABLE Android_Data_Stat_Per_Day SELECT meta_owner as owner, meta_deviceId as deviceId, type, sum(total) as total, year, month, day, getDateStartingTime(year, month, day) as timestamp FROM AndroidDataData GROUP BY year, month, day, meta_deviceId, meta_owner, type ORDER BY timestamp DESC;
INCREMENTAL_TABLE_COMMIT ORG_WSO2_IOT_ANDROID_DATA_STATS;
