Analytics.Script:
CREATE TEMPORARY TABLE DevicegeolocationData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_geolocation");
CREATE TEMPORARY TABLE DevicegeolocationSummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_geolocation_SUMMARY", schema "deviceType STRING -i, deviceId STRING -i, owner STRING -i, time LONG -i, latitude DOUBLE -i,longitude DOUBLE -i, type STRING -i,speed FLOAT -i, heading FLOAT -i, state STRING -i, information STRING -i, notify BOOLEAN -i",primaryKeys "deviceType, deviceId, owner, time");
insert into table DevicegeolocationSummaryData select meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time/1000 as BIGINT)as time, latitude,longitude,type, speed,heading, state, information, notify from DevicegeolocationData group by latitude,longitude,type, speed,heading,state, information, notify, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time/1000 as BIGINT);
